			result[1] = BigInteger.ONE;
			BigInteger runningSum = BigInteger.ONE;
			for(int i4=2; i4<=B; i4++) {
			    result[i4] = runningSum;
			    runningSum = runningSum.add(result[i4]);
			}
            // System.out.println(B + "");
            // System.out.println(M.toString());
