package googlejam9.p035;

import java.util.Scanner;
import java.util.ArrayList;

public class Main
{
	public static void main(String[] args)
	{
		Scanner sc = new Scanner(System.in);
		int tests = sc.nextInt();
		for(int testNum = 1; testNum <= tests; testNum++)
		{
			System.out.print("Case #" + testNum + ": ");
			int buildings = sc.nextInt();
			long ways = sc.nextLong();

			if(ways > Math.pow(2, buildings - 2))
				System.out.println("IMPOSSIBLE");
			else if(buildings == 2)
			{
				//Special case for n = 2
				System.out.println("POSSIBLE");
				if(ways == 0)
					System.out.println("00\n00");
				else if(ways == 1)
					System.out.println("01\n00");
			}
			else if(ways == Math.pow(2, buildings - 2))
			{
				System.out.println("POSSIBLE");
				for(int i = 0; i < buildings; i++)
				{
					for(int j = 0; j < buildings; j++)
					{
						if(i < j)
							System.out.print("1");
						else
							System.out.print("0");
					}
					System.out.println();
				}
			}
			else
			{
				System.out.println("POSSIBLE");

				int[][] edges = new int[buildings][buildings];
				//Create 2^(i - 1) ways to reach building i
				for(int i = 0; i < buildings - 1; i++)
				{
					for(int j = i + 1; j < buildings - 1; j++)
						edges[i][j] = 1;
				}

				//Find binary representation to add edges to last building
				for(int i = 0; i < buildings - 1; i++)
				{
					if((1L << i & ways) > 0)
						edges[i + 1][buildings - 1] = 1;
				}

				for(int i = 0; i < buildings; i++)
				{
					for(int j = 0; j < buildings; j++)
					{
						System.out.print(edges[i][j]);
					}
					System.out.println();
				}

				//countWays(edges);
			}
		}
	}
}

package googlejam9.p069;
import java.io.File;
import java.io.FileNotFoundException;
import java.util.Scanner;

public class B {

    public static void main(String[] args) throws FileNotFoundException {
        //Scanner scanner = new Scanner(System.in);
        Scanner scanner = new Scanner(new File("inputBlarge.txt"));
        int tests = scanner.nextInt();
        for (int test=1; test<=tests; test++) {
            scanner.nextLine();
            int b = scanner.nextInt();
            long m = scanner.nextLong();
            int[][] ans = new int[b+1][b+1];

            ans[1][b] = 1;
            m--;
            if (m > 0 && 2 < b) {
                ans[2][b] = 1;
                m--;
            }

            for (int i=2; i<b; i++) {
                ans[1][i] = 1;
            }

            int next = 3;
            long add = 2L;
            while (m > 0 && next < b) {
                ans[next][b] = 1;
                for (int prev = next - 1; prev >= 2; prev--) {
                    ans[next][prev] = 1;
                }
                m -= add;
                add *= 2;
                next++;
            }
            add /= 4;
            int last = next - 1;
            int prev = last - 1;
            while (m < 0) {
                while (add > -m) {
                    prev--;
                    add /= 2;
                }
                m += add;
                ans[last][prev] = 0;
            }

            if (m > 0) {
                System.out.println("Case #" + test + ": IMPOSSIBLE");
            } else {
                System.out.println("Case #" + test + ": POSSIBLE");
                for (int y=1; y<ans.length; y++) {
                    for (int x=1; x<ans[0].length; x++) {
                        System.out.print(ans[y][x]);
                    }
                    System.out.println();
                }
            }
        }

        scanner.close();
    }
}

package googlejam9.p198;
import java.util.Scanner;
import java.io.FileInputStream;
import java.io.PrintStream;

public class Main {
    public static void main(String[] args) throws Exception {
        // set up scanner and iterate over test cases
        Scanner scanner = new Scanner(new FileInputStream(
            "C:\\Users\\Stefan\\Downloads\\B-small-attempt1.in"));
        PrintStream output = new PrintStream(
            "C:\\Users\\Stefan\\Downloads\\B-small-attempt1.out");
        int T = scanner.nextInt();
        for (int t = 1; t <= T; ++t) {
            // get parameters
            int B = scanner.nextInt();
            int M = scanner.nextInt();
            // solve problem
            output.print("Case #" + t + ":");
            // there are no cycles in graph
            // determine if it is possible, only depends on maximum
            long maxWays = 0x01 << (B - 2);
            if (M > maxWays) {
                output.println(" IMPOSSIBLE");
                continue;
            }
           	output.println(" POSSIBLE");
           	// print solution matrix
           	// first node: construct M by using counts of 2 ^ X
           	if (B == 2)
           		output.println("01");
           	else {
           		long maxNodeWays = 0x01 << (B - 3);
           		output.print("0");
                for (int slide = 1; slide < B - 1; ++slide) {
                	if (M >= maxNodeWays) {
                   		output.print("1");
                   		M -= maxNodeWays;
                	}
                	else
                		output.print("0");
               		maxNodeWays /= 2;
                }
           		if (M > 0)
           			output.println("1");
           		else
           			output.println("0");
           	}
           	// output slides from building b towards any other building
        	// make slides between all nodes except 1 and B
            for (int b = 1; b < B - 1; ++b) {
	            for (int slide = 0; slide < B; ++slide)
	            	if (slide <= b)
	            		output.print("0");
	            	else
	            		output.print("1");
	           	output.println();
            }
            // no slides from last node
            for (int slide = 0; slide < B; ++slide)
        		output.print("0");
           	output.println();
        }
        output.close();
        System.out.println("finished");
    }
}

