public class Be
{
	public static void main(String[] args) throws Exception
	{
		out = new PrintWriter(new FileWriter("outB.txt"));
		int testCases = Integer.parseInt(next());
		for (int t = 1; t <= testCases; t++)
		{
			int b = Integer.parseInt(next());
			long m = Long.parseLong(next());

			if (Math.pow(2, b - 2) < m)
				out.printf("Case #%d: IMPOSSIBLE%n", t);
			else
			{
				int[][] g = new int[b][b];
				for (int i = 1; i < b; i++)
					g[0][i] = 1;

				long c = 1;

				long[] p = new long[b];
				p[b - 1] = 1;

				for (int i = b - 2; i >= 0; i--)
					for (int j = i + 1; j < b; j++)
					{
						if (c == m)
							break;

						if (c + p[j] <= m)
						{
							g[i][j] = 1;
							p[i] += p[j];
							c += p[j];
						}
					}

				if (c == m)
				{
					out.printf("Case #%d: POSSIBLE%n", t);
					for (int i = 0; i < b; i++)
					{
						for (int j = 0; j < b; j++)
							out.print(Math.min(1, g[i][j]));
						out.println();
					}
				}
				else
					out.printf("Case #%d: IMPOSSIBLE%n", t);
			}
		}
		out.close();
	}

	static BufferedReader br = new BufferedReader(new InputStreamReader(
			System.in));
	static PrintWriter out = new PrintWriter(new BufferedWriter(
			new OutputStreamWriter(System.out)));
	static StringTokenizer st;

	static String next() throws IOException
	{
		while (st == null || !st.hasMoreTokens())
			st = new StringTokenizer(br.readLine().trim());
		return st.nextToken();
	}

}

