import java.util.Scanner;

public class B {
	public static void main(String[] args) throws Exception {
		Scanner in = new Scanner(new File("BL.in"));
		PrintWriter out = new PrintWriter("BL.out");
		long[] counts = new long[55];
		counts[0] = 1;
		for (int i = 1; i < 55; i++) {
			for (int j = 0; j < i; j++) {
				counts[i] += counts[j]; // 2^(i-1)
			}
		}
		int tc = in.nextInt();
		for (int cc = 1; cc <= tc; cc++) {
			int b = in.nextInt();
			long t = in.nextLong();
			char[][] result = new char[b][b];
			for (char[] a : result)
				Arrays.fill(a, '0');
			for (int i = b - 1; i > 0; i--) {
				for (int j = i + 1; j < b; j++) {
					result[i][j] = '1';
				}
			}
			if (t > counts[b - 1]) {
				System.out.printf("Case #%d: IMPOSSIBLE\n", cc);
				out.printf("Case #%d: IMPOSSIBLE\n", cc);

			} else {
				System.out.printf("Case #%d: POSSIBLE\n", cc);
				out.printf("Case #%d: POSSIBLE\n", cc);

				if (t == counts[b - 1]) {
					for (int i = 1; i < b; i++) {
						result[0][i] = '1';
					}
				} else {
					while (t > 0) {
						long bit = t & -t;
						int idx = 0;
						while (1L << idx < bit)
							idx++;
						if (idx >= b) {
							throw new Exception("That's how you know you ....ed up");
						}
						result[0][b - 1 - (idx + 1)] = '1';
						t -= bit;
					}
				}
				for (int i = 0; i < b; i++) {
					System.out.printf(new String(result[i]) + "\n");
					out.printf(new String(result[i]) + "\n");

				}
			}
		}
		out.close();
	}
}

package googlejam9.p120;

import java.util.*;

public class B {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int T = in.nextInt();

        for (int t = 1; t <= T; ++t) {
            int n = in.nextInt();
            long m = in.nextLong();
            boolean[][] b = new boolean[n][n];
            long[] sum = new long[n];
            sum[0] = 1;
            b[0][n - 1] = true;
            --m;
            int cur = 1;
            while (m > 0 && cur < n - 1) {
                b[cur][n - 1] = true;
                b[0][cur] = true;
                --m;
                sum[cur]++;
                for (int prev = cur - 1; prev > 0; --prev) {
                    if (m >= sum[prev]) {
                        b[prev][cur] = true;
                        m -= sum[prev];
                        sum[cur] += sum[prev];
                    }
                }
                ++cur;
            }

            if (m > 0) {
                System.out.println("Case #" + t + ": IMPOSSIBLE");
            } else {
                System.out.println("Case #" + t + ": POSSIBLE");
                for (int i = 0; i < n; ++i) {
                    for (int j = 0; j < n; ++j) {
                        System.out.print(b[i][j] ? 1 : 0);
                    }
                    System.out.println();
                }
            }
        }
    }
}

package googlejam9.p191;
import java.math.BigInteger;
import java.util.Scanner;

public class CodeJamCB {
	public static void main(String []args){
		Scanner reader = new Scanner(System.in);
		int g = reader.nextInt();
		for(int k=0;k<g;k++){
			int b = reader.nextInt();
			long m = reader.nextLong();
			BigInteger c = new BigInteger(m+"");
			BigInteger two = new BigInteger("2");
			BigInteger rez = two.pow(b-2);
			if(rez.compareTo(c)<0){
				System.out.println("Case #"+(k+1)+": IMPOSSIBLE");
			}
			else{
				System.out.println("Case #"+(k+1)+": POSSIBLE");
				BigInteger e = new BigInteger(m+"");
				int max = lo(m)+1;
				int [][] t = new int[b][b];
				for(int i=0;i<=max;i++){
					for(int j=i+1;j<=max;j++){
						t[i][j] = 1;
					}
				}

				while(m>0){
					if(m==1){
						t[0][b-1] = 1;
						m--;
					}
					else{
						int log = lo(m)+1;
						t[log][b-1] = 1;
						m-= Math.pow(2, log-1);
					}
				}
				for(int i=0;i<b;i++){
					t[b-1][i] = 0;
				}


				for(int i=0;i<t.length;i++){
					for(int j=0;j<t.length;j++){
						System.out.print(t[i][j]);
					}
					System.out.println();
				}
			}
		}


		reader.close();
	}
	public static int lo(long a){
		double c = (double) a;
		double rez = Math.log(c)/Math.log(2);
		return (int) rez;
	}
}

