				for(int i =0; i<builds;i++){
					for(int j = 0;j<=i;j++){
						strings[i]+="0";
					}
					for(int j = i+1;j+1<builds;j++){
						strings[i]+="1";
					}
				}
				for(int i = builds-2;i>=0;i--){
					long can = 1;
					if(i>0){
						can = (1L<<i-1);
					}
					if(can<=ways){
						ways-=can;
						strings[i]+="1";
					}
					else{
						strings[i]+="0";
					}
				}
				for(int i =0;i<builds; i++){
					System.out.println(strings[i]);
				}
			}
		}
	}
}

