package googlejam9.p057;


import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.util.Scanner;

public class Slides {
    public static void main(String[] args) {
        Scanner in = new Scanner(new BufferedReader(new InputStreamReader(System.in)));
        int t = in.nextInt(); 
        for (int i = 1; i <= t; ++i) {
            int b = in.nextInt();
            long m = in.nextLong();
            long p = pow(2,b-2);
            if (p<m){
                System.out.println("Case #" + i + ": IMPOSSIBLE");
            } else {
                System.out.println("Case #" + i + ": POSSIBLE");
                if (p==m){
                    for (int j1=0; j1<b; ++j1){
                        String s = "";
                        for (int j2 = 0; j2<b; ++j2){
                            if (j2<=j1) s += "0";
                            else s += "1";
                        }
                        System.out.println(s);
                    }
                } else {
                    for (int j1=0; j1<b; ++j1){
                        String s = "";
                        for (int j2 = 0; j2<b-1; ++j2){
                            if (j2<=j1) s += "0";
                            else s += "1";
                        }
                        if (j1 == 0) s += "0";
                        else {
                            if (m%2==1) s += "1";
                            else s += "0";
                            m /= 2;
                        }
                        System.out.println(s);
                    }
                }
            }
        }
        in.close();
    }

    public static long pow(long a, long b){
        if (b==0) return 1;
        else if (b%2==0) return pow(a*a,b/2);
        else return a*pow(a*a,b/2);
    }
}

package googlejam9.p118;
import java.io.File;
import java.io.FileNotFoundException;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.Scanner;

/**
 * Created by fleeon on 5/8/16.
 */
public class B {

    public static void main(String[] args) throws FileNotFoundException {
        Scanner scanner = new Scanner(new File("B.in"));
        PrintWriter writer = new PrintWriter("B.out");
        int T = scanner.nextInt();
        for (int t = 0; t < T; t++) {
            int B = scanner.nextInt();
            long M = scanner.nextLong();
            long bi[] = new long[B + 1];
            long now = 1, sum = now;
            bi[1] = now;
            for (int b = 2; b < B; b++) {
                now = Math.min(now, M - sum);
                if (now == 0) break;
                bi[b] = now;
                sum += now;
                now *= 2;
            }
            bi[B] = sum;
//            writer.println(Arrays.toString(bi));

            writer.printf("Case #%d: ", t + 1);
            if (bi[B] < M) {
                writer.println("IMPOSSIBLE");
            } else {
                writer.println("POSSIBLE");
                int res[][] = new int[B + 1][B + 1];
                for (int i = 1; i <= B; i++) {
                    sum = bi[i];
                    for (int j = i - 1; j >= 1 && sum > 0; j--) {
                        if (bi[j] == 0 || sum - bi[j] < 0) continue;
                        sum -= bi[j];
                        res[j][i] = 1;
                    }
                }
                for (int i = 1; i <= B; i++) {
                    for (int j = 1; j <= B; j++) {
                        writer.print(res[i][j]);
                    }
                    writer.println();
                }
            }
        }
        writer.close();
    }
}

