                sum += (C - W) / W;
                sum += W;

                if (C % W != 0) {
                    sum++;
                }




                str.append("Case #").append(i).append(": ").append(sum);

                System.out.println(str.toString());

                writer.write(str.toString());
                writer.newLine();

                str = new StringBuilder();
            }
        }

