				int steps = R*(C/W) + W - 1; // R*(C/W) + (W-1)
				if (C % W != 0)
					steps++;
				writer.write(steps + "");
				writer.write("\n");
			}catch (IOException e) {
				System.out.println("Error on trial #" + trial + "\n" + e.getMessage());
				e.printStackTrace();
			}
		}
		try {
			writer.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
		scanner.close();
		System.out.println("Completed Trials!");
	}
}

