				int W = scan.nextInt();
				int C = CC;
				System.out.println(R + " " + C + " " + W);
				int result = 0;
				result += C / W * (R - 1);
				System.out.println(C + " > " + (2 * W + 1));
				while (C > 2 * W) {
					C -= W;
					result++;
				}
				System.out.println((W+1) + " " + result);

				if (C == W)
					result += W;
				else
					result += W + 1;
				System.out.println((W+1) + " " + result);

				output.println("Case #" + (i + 1) + ": " + result);
				System.out.println("Case #" + (i + 1) + ": " + R + " " + CC + " " + W + "\t result :" + result);
			}
			scan.close();
			output.close();
