			c = Integer.parseInt(arr[1]);
			w = Integer.parseInt(arr[2]);

			if(c == w)
			{
				ans = c;
			}
			else if(w==1)
			{
				ans = c;
			}
			else
			{
				ans += c/w;
				if ( ans*w == c)
				{
					ans--;
				}
				ans += w;
			}
			ans = ans * r;

			System.out.println("Case #"+i+": "+ans);
			i++;
		}
	}
}

