				writer = new PrintWriter("A-large.out");

				int counter = 0;
				for(int i=0; i<so.res.size();++i)
				{
					writer.print("Case #" + (++counter) + ": " + so.res.get(i));
					if(counter<so.res.size())
					{
						writer.println();
					}
				}

				writer.close();
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			}
		}

