					int[] a= new int[tokens.countTokens()];
					int i1 = 0;
					while(tokens.hasMoreElements()) {
					    a[i1++] = Integer.parseInt(tokens.nextToken());
					}
					int[] params = a;
					int rows = params[0];
					int cols = params[1];
					int width = params[2];

					int count1 = 0;

					int perrow = cols/width;

					count1 = perrow * rows;

