        printPath (mat, i + 1, j);

    } else if (j >= 0 && j < mat [0].length - 1 && mat [i] [j] < mat [i] [j + 1]) {

        printPath (mat, i, j + 1);

    } else if (i > 0 && i < mat.length - 1 && mat [i] [X1] < mat [i - 1] [j]) {

        printPath (mat, i - 1, j);

