            if (originalAutoCommit != this.autoCommit) {

                connection.setAutoCommit (this.autoCommit);

            }

            X1 (connection, reader);

        } finally {

            connection.setAutoCommit (originalAutoCommit);

        }

    } catch (IOException e) {

