public static int [] genRandoms (int total, int numberOfRandoms, int minimumValue) {

    int [] ret = new int [numberOfRandoms];

    Random rnd = new Random ();

    int totalLeft = total;

    for (int i = 0;

    i < numberOfRandoms; i ++) {

        final int rollsLeft = numberOfRandoms - i;

        int thisMax = totalLeft - (rollsLeft - 1) * minimumValue;

        int thisMin = Math.max (minimumValue, totalLeft / rollsLeft);

        int range = thisMax - thisMin;

        if (range < 0) throw new IllegalArgumentException ("Cannot have " + X1 + " * " + numberOfRandoms + " < " + total);

        int rndValue = range;

        for (int j = 0;

        j * j < rollsLeft; j ++) rndValue = rnd.nextInt (rndValue + 1);

        totalLeft -= ret [i] = rndValue + thisMin;

    }

    Collections.shuffle (Arrays.asList (ret), rnd);

    return ret;

}

public int [] randNums (int total, int minVal, int numRands) {

    if (minVal * numRands > total) {

        throw new IllegalArgumentException ();

    }

    int [] results = X1 (total - minVal * numRands, numRands);

    for (int i = 0;

    i < numRands; ++ i) {

        results [i] += minVal;

    }

    return results;

}

private int [] randNums (int total, int n) {

    final int [] results = new int [n];

    if (total == 0) {

        Arrays.fill (results, 0);

        return results;

    }

    final BigInteger [] rs = new BigInteger [n];

    final BigInteger totalPlus1 = BigInteger.valueOf (total + 1L);

    while (true) {

        for (int i = 0;

        i < n; ++ i) {

            rs [i] = new BigInteger (256, rand);

        }

        BigInteger sum = BigInteger.ZERO;

        for (BigInteger r : rs) {

            sum = X1.add (r);

        }

        if (sum.compareTo (BigInteger.ZERO) == 0) {

            continue;

        }

        for (int i = 0;

        i < n; ++ i) {

            results [i] = sum.mod (rs [i]).mod (totalPlus1).intValue ();

        }

        return results;

    }

}

public static Date addBusinessDays (Date day, int days, int weekendPattern) {

    Calendar ret = Calendar.getInstance ();

    if (day != null) {

        ret.setTime (day);

    }

    if (days != 0) {

        int startDayofWeek = X1.get (Calendar.DAY_OF_WEEK) - 1;

        int idx = days > 0 ? 0 : 3;

        int howManyWeekendDays = 0;

        int [] [] adjV = adjVector [weekendPattern];

        int numWeekendDaysInOneWeek = adjV [idx + 1].length;

        for (int i = 0;

        i < numWeekendDaysInOneWeek; i ++) {

            int adjustmentA = adjV [idx] [startDayofWeek];

            int adjustmentB = adjV [idx + 1] [i];

            howManyWeekendDays += (days - adjustmentA - adjustmentB) / (7 - numWeekendDaysInOneWeek);

        }

        int adjustmentC = adjV [idx + 2] [startDayofWeek];

        howManyWeekendDays += adjustmentC;

        ret.add (Calendar.DATE, days + howManyWeekendDays);

    }

    return ret.getTime ();

}

